/* 
Given a string s and a string t, check if s is subsequence of t.
You may assume that there is only lower case English letters in both s and t. t is potentially a very long (length ~= 500,000) string, and s is a short string (<=100).
A subsequence of a string is a new string which is formed from the original string by deleting some (can be none) of the characters without disturbing the relative positions of the remaining characters. (ie, "ace" is a subsequence of "abcde" while "aec" is not).
Example 1:
s = "abc", t = "ahbgdc"
Return true.
Example 2:
s = "axc", t = "ahbgdc"
Return false.
Follow up:
If there are lots of incoming S, say S1, S2, ... , Sk where k >= 1B, and you want to check one by one to see if T has its subsequence. In this scenario, how would you change your code?
*/

#include <iostream>
#include <string>

using namespace std;

bool isSubsequence(string s, string t) {
	if (s == t || s.empty())
		return true;
		
	int i = 0, j = 0, s_end = s.size()-1, t_end = t.size()-1;
	if (s_end > t_end)
		return false;
		
	while (i != s_end && j != t_end)
	{
		if (s[i] != t[j])
			j++;
		else
		{
			i++;
			j++;
		}
	}
	if (j < t_end) // t not end
		return true;
	else if (i < s_end) // s not end
		return false;
	else // both end
	{
		if (s[s_end] == t[t_end])
			return true;
		else
			return false;
	}
}

bool isSubsequence2(string s, string t) 
{
	if (s == t || s.empty())
		return true;
	
	int pos = 0;
	for (int i = 0; i < s.size(); i++)
	{
		char temp = s[i];
		pos = t.find(temp, pos);
		if (pos == -1) // not found
			return false;
		pos++;
	}
	return true;
}

int main()
{
	//string s = "abc", t = "ahbgdc";
	//string s = "acb", t = "ahbgdc";
	string s = "leeeeetcode";
	string t = "";
	if (isSubsequence2(s, t))
		cout<<"is subsequence"<<endl;
	else
		cout<<"is NOT subsequence"<<endl;
	
	return 0;
}
